
public class Main04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int plus_num = 1;
		plus_num = plus_num + 1;
		plus_num +=1;
		plus_num++;
		++plus_num;
		System.out.println(plus_num);
	
		int minum_num = 5;
		minum_num = minum_num-1;
		minum_num -= 1;
		minum_num--;
		--minum_num;
		System.out.println(minum_num);
	}

}
