
public class Main01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int myage = 19;
		// 비교는 =이 두개
		if (myage == 19) {
			System.out.println("19살 입니다.");
		}
		if (myage != 20) {
			System.out.println("20살이 아닙니다.");
		}
		if (myage > 19) {
			System.out.println("성인입니다.");
		}
		if (myage <= 19) {
			System.out.println("학생입니다.");
		}
	}

}
